from typing import List

class Cashier:

    def __init__(self, n: int, discount: int, products: List[int], prices: List[int]):
        self.lucky = n
        self.discount = discount
        self.costs = {}
        self.customers = 0

        for i in range(len(products)):
            self.costs[products[i]] = prices[i]
        

    def getBill(self, product: List[int], amount: List[int]) -> float:
        self.customers += 1
        bill = sum(self.costs[product[i]]*amount[i] for i in range(len(product)))

        if self.customers % self.lucky == 0:
            print(self.customers, 'customers')
            bill *= (100-self.discount) / 100
            print(bill)
        
        return bill
        

